import {React, useEffect, useState} from "react";
import { Link ,useLocation, useNavigate} from 'react-router-dom';


const Navbar=()=>{

//creating a instance of location 
let location=useLocation();

const navigate=useNavigate();

const [logoutclicked,setLogout]=useState(true);

//to hide login and signup after to login or signup page
const [isclicked,setclicked]=useState(false);
const HideLogin=()=>{

setclicked(true);
}


useEffect(()=>{

  const token = localStorage.getItem('auth_token');
  setIsLoggedIn(token && token !== 'null');



},[location])
const HandleLogout=()=>{

  localStorage.setItem('auth_token',null);
  console.log(localStorage.getItem('auth_token'));
 setLogout(true);
  navigate("/login");

}


// useEffect(() => {
//   // Google Analytics
// }, [location]);



return(
<>



<nav className="navbar navbar-expand-lg navbar-dark  bg-dark" style={{height:'56px'}}>
  <div className="container-fluid">
    <Link className="navbar-brand" to="/">iNoteBook</Link>
    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span className="navbar-toggler-icon"></span>
    </button>
    <div className="collapse navbar-collapse" id="navbarSupportedContent">
      <ul className="navbar-nav me-auto mb-2 mb-lg-0">
        <li className="nav-item">
          <Link className={`nav-link ${location.pathname==='/'? "active":""}`} aria-current="page" to="/">Home</Link>
        </li>
        <li className="nav-item">
          <Link className={`nav-link ${location.pathname==='/notes' ? "active":""}`} to="/notes">Notes</Link>
        </li>
        <li className="nav-item">
          <Link className={`nav-link ${location.pathname==='/about' ? "active":""}`} to="/about">About</Link>
        </li>
        <li   className="nav-item dropdown">
          <a className="nav-link dropdown-toggle" href="/" role="button" data-bs-toggle="dropdown" aria-expanded="false">
            Dropdown
          </a>
          <ul className="dropdown-menu">
            <li><a className="dropdown-item" href="/">Action</a></li>
            <li><a className="dropdown-item" href="/">Another action</a></li>
            <li><hr className="dropdown-divider"/></li>
            <li><a className="dropdown-item" href="/">Something else here</a></li>
          </ul>

        </li>
      </ul>
      {(localStorage.getItem('auth_token')!==null) ? 
    <Link to="/"><button hidden={(logoutclicked) ? true:false} onClick={HandleLogout} type="button" className="btn btn-primary mx-2"  style={{height:'40px',borderRadius:'0.375rem',width:'81.05px'}}>Logout</button></Link>
    : 
      <form form className="d-flex" role="search" style={{height:'40px'}}>
        <Link to="/signup"><button  hidden={isclicked===true ? true:false} onClick={HideLogin} type="button" className="btn btn-primary mx-2"  style={{height:'40px',borderRadius:'0.375rem',width:'81.05px'}}>Signup</button></Link>
       <Link to="/login"> <button  hidden={isclicked===true ? true:false} onClick={HideLogin} className="btn btn-outline-success" type="submit" style={{height:'40px',borderRadius:'0.375rem',width:'81.05px'}}>Login</button></Link></form>
}
    
    </div>
  </div>
</nav>

</>





);



}

export default Navbar;
