import React ,{useContext} from "react";
import NoteContext from "../context/noteContext";
import NoteItems from "./NoteItems";
import img from '../assets/addnote.png'
import { Link } from "react-router-dom";

const Notes=()=>{

    const context= useContext(NoteContext);
 

    //destructing of context values
    const {notes,addnote}=context;

return(

<>


<Link to="/createnote"><button type="button" className="btn btn_danger" style={{position:'sticky',top:'82vh',left:'91.5vw',background:'ghostwhite',borderRadius:'1.2rem',padding:'3px',paddingRight:'0px'}}>
       <img src={img} height={"65px"} width={"65px"}></img>
  </button></Link>

  <h3 className="my-2 mt-4" style={{textAlign:'center' ,color:'white'}}>Your Notes</h3>

  <div className="container"style={{display:"grid",gridTemplateColumns:"1fr 1fr"}}>


  {notes.map((note)=>{
    
    
    return(

      <NoteItems key={note._id} note={note} />

    )
    
    
  })}


</div>





{/* <Route exact path="/createnote" element={<Addnote/>}/> */}




</>







);












}

export default Notes;
