      }


    ]

    
    const [notes,setnotes]=useState(initalNotes);


//function to add notes
const addNote = (title, description, tag) => {

  //api call to add note
  const note = {
    _id: Math.random().toString(36).substr(2, 9), // fake unique id
    user: "some-user-id", // you can replace this with the actual user ID
    title,
    description,
    tag,
    date: new Date().toISOString()
    // __v: 0
  };

  console.log("Adding a new note:", note);
  setnotes(notes.concat(note));
};

//function to editnote
const editNote=(id,title,tag,description)=>{

  for(let i=0;i<notes.length;i++){

    if(notes[i]._id===id){
      const element=notes[i];
     
      console.log(notes[i]);
       element.title=title;
       element.tag=tag;
       element.description=description;
    }

  };




}



//function to store the id of note to be delete
const deleteNote=(id)=>{

delete_note_id=id;

};






//to delete note when only confirm deletion is clicked 
const ConfirmDeleteNote=()=>{

  // api call for deletion
  
  const NewNotes=notes.filter((note)=>{
    return (note._id!==delete_note_id)});
  setnotes(NewNotes);
};




    return(

<NoteContext.Provider value={{notes,setnotes,addNote,editNote,deleteNote,ConfirmDeleteNote}}>

{props.children}
    
</NoteContext.Provider>


    );
   




    
  }

export default NoteState;
