import React,{useContext, useState} from "react";
import NoteContext from "../context/noteContext";
import Notes from "./Notes";

const Addnote=()=>{

    const context= useContext(NoteContext);


    const {addNote}=context;

    const [note,setNote]=useState({title:"",tag:"",description:""});
    
    //to handle the addnotes button
    const handleClick = (e) => {
        e.preventDefault(); // ðŸ”´ prevent page reload
        const { title, description, tag } = note;
    
        if (!title.trim() || !description.trim()) {
          alert("Title and Description are required.");
          return;
        }
    
        addNote(title, description, tag); // âœ… send separate values
        setNote({ title: "", tag: "", description: "" }); // reset form
      };
    
    
    //function to handle the changes to the  input note
    const onChange=(e)=>{
        

        setNote({...note,[e.target.name]:e.target.value});
    





    }




return (
<>

<div className="mx-5 container" style={{width:'60%' ,display:'flex',flexDirection:'column',alignItems: 'center', color:'white', backgroundColor:' #0093E9',backgroundImage:'linear-gradient(160deg, #003366 31%, #2877b1 100%)',borderRadius:'5.375rem'}}>

<h2 className="text-center mt-4">Add Notes</h2>

<form style={{width:'80%'}}>
  <div className="mb-3">
    <div id="emailHelp" style={{color:'#4dad80',textAlign:'center'}} className="form-text container">We'll never share your notes with anyone else.</div>
    <label htmlFor="title" className="form-label">Title</label>
    <input type="text" className="form-control" onChange={onChange} id="title" name="title" aria-describedby="emailHelp"/>
    <div id="emailHelp" style={{color:'#c04a55'}} className="form-text">* Title is required.</div>
  </div>
  <div className="mb-3">
    <label htmlFor="tag" className="form-label">Tag</label>
    <input type="text" className="form-control" onChange={onChange} id="tag"  name="tag" aria-describedby="emailHelp"/>
    <div id="emailHelp" style={{color:'#c04a55'}} className="form-text">* Tag is required.</div>
  </div>
  <div className="mb-3">
    <label htmlFor="description" className="form-label">Description</label>
    <textarea type="text" className="form-control" onChange={onChange} id="description" name="description"/>  
    {/* background-color: #0093E9;
background-image: linear-gradient(160deg, #0093E9 31%, #80D0C7 100%); */}

    <div id="emailHelp" style={{color:'#c04a55'}} className="form-text">* Description is required.</div>
  </div>
  <div className="mb-3 form-check">
    <input type="checkbox" className="form-check-input" id="exampleCheck1"/>
    <label className="form-check-label" htmlFor="exampleCheck1">Check me out</label>
  </div>
  <button type="submit" style={{marginBottom:'0.85rem'}} className="btn btn-primary" onClick={handleClick}>Add note</button>
</form>




</div>





<Notes />

</>

)




}

export default Addnote;
