import React ,{useContext,useState} from "react";
import NoteContext from "../context/noteContext";
import NoteItems from "./NoteItems";
import img from '../assets/addnote.png'
import { Link } from "react-router-dom";

const Notes=()=>{

    const context= useContext(NoteContext);
 

    //destructing of context values
    const {notes,addnote}=context;
    
    
    
    const [recipient, setRecipient] = useState('');
    const [showModal, setShowModal] = useState(false);
    
    const handleButtonClick =(data) => {
      setRecipient(`${data}`);
      // updateNote=recipient;
      setShowModal(true);
      document.body.style.overflow = 'hidden'; //disable scrolling 
    };
    //function to handle edit button ;
  
    const handleClose = () => {
      setShowModal(false);
      document.body.style.overflow = 'auto'; // Enable scrolling again

    };
  
    

return(
<>





        {/* Trigger button */}
        <button 
          onClick={() => handleButtonClick('John Doe')}
          className="btn btn-primary"
        >
          Open Modal
        </button>
  
        {/* Modal */}
        {showModal && (
          <>

<div
className="modal-backdrop"
style={{
  position: 'fixed',
  top: 0,
  left: 0,
  width: '100vw',
  height: '100vh',
  backgroundColor: 'rgba(0, 0, 0, 0.5)', // semi-transparent dark
  zIndex: 1040
}}
></div>



          <div className="modal show d-block" tabIndex="-1" role="dialog" style={{ zIndex: 1050 }}>
            <div className="modal-dialog" role="document">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title">Title:{recipient}</h5>
                  <button type="button" className="close" onClick={handleClose}>
                    <span>&times;</span>
                  </button>
                </div>
                <div className="modal-body">
                  <label for="new_title">Title</label>
                  <input 
                    type="text" id="new_title" name="new_title"
                    className="form-control" 
                    value={{}}
                    onChange={(e) => setRecipient(e.target.value)}
                  />
                </div>
                <div className="modal-body">
                  <label for="new_tag">Tag</label>
                  <input 
                    type="text" id="new_tag" name="new_tage"
                    className="form-control" 
                    value={{}}
                    onChange={(e) => setRecipient(e.target.value)}
                  />
                </div>
                <div className="modal-body">
                  <label for="new_description">Description</label>
                  <textarea 
                    type="text" id="new_description" name="new_description" 
                    className="form-control" 
                    value={{}}
                    onChange={(e) => setRecipient(e.target.value)}
                  />
                </div>
                <div className="modal-footer">
                <button onClick={handleButtonClick} to="/notes/delete" style={{backgroundColor:'', borderRadius:'1.375rem'}}  className="btn btn-danger mb-2"><svg xmlns="http://www.w3.org/2000/svg" width="22" height="22" fill="currentColor" class="bi bi-x-circle-fill" viewBox="0 0 16 16">
  <path d="M16 8A8 8 0 1 1 0 8a8 8 0 0 1 16 0M5.354 4.646a.5.5 0 1 0-.708.708L7.293 8l-2.647 2.646a.5.5 0 0 0 .708.708L8 8.707l2.646 2.647a.5.5 0 0 0 .708-.708L8.707 8l2.647-2.646a.5.5 0 0 0-.708-.708L8 7.293z"/>
</svg></button>
                  <a href="/" style={{backgroundColor:'#3a7ee3', borderRadius:'1.375rem'}}  className="btn btn-primary mb-2"><svg xmlns="http://www.w3.org/2000/svg" width="22" height="22" fill="currentColor" className="bi bi-cloud-upload-fill" viewBox="0 0 16 16">
                         <path fillRule="evenodd" d="M8 0a5.53 5.53 0 0 0-3.594 1.342c-.766.66-1.321 1.52-1.464 2.383C1.266 4.095 0 5.555 0 7.318 0 9.366 1.708 11 3.781 11H7.5V5.707L5.354 7.854a.5.5 0 1 1-.708-.708l3-3a.5.5 0 0 1 .708 0l3 3a.5.5 0 0 1-.708.708L8.5 5.707V11h4.188C14.502 11 16 9.57 16 7.773c0-1.636-1.242-2.969-2.834-3.194C12.923 1.999 10.69 0 8 0m-.5 14.5V11h1v3.5a.5.5 0 0 1-1 0"/>
                           </svg></a>

                </div>
              </div>
            </div>
          </div>
          </>
        )}

{/* <button type="button" className="btn btn-primary" data-toggle="modal" data-target="#exampleModal" data-whatever="@mdo">Open modal for @mdo</button>
<button type="button" className="btn btn-primary" data-toggle="modal" data-target="#exampleModal" data-whatever="@fat">Open modal for @fat</button>
<button type="button" className="btn btn-primary" data-toggle="modal" data-target="#exampleModal" data-whatever="@getbootstrap">Open modal for @getbootstrap</button>

<div className="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div className="modal-dialog" role="document">
    <div className="modal-content">
      <div className="modal-header">
        <h5 className="modal-title" id="exampleModalLabel">New message</h5>
        <button type="button" className="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div className="modal-body">
        <form>
          <div className="form-group">
            <label for="recipient-name" className="col-form-label">Recipient:</label>
            <input type="text" className="form-control" id="recipient-name"/>
          </div>
          <div className="form-group">
            <label for="message-text" className="col-form-label">Message:</label>
            <textarea className="form-control" id="message-text"></textarea>
          </div>
        </form>
      </div>
      <div className="modal-footer">
        <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
        <button type="button" className="btn btn-primary">Send message</button>
      </div>
    </div>
  </div>
</div> */}





<Link to="/createnote"><button type="button" className="btn btn_danger" style={{position:'sticky',top:'82vh',left:'91.5vw',background:'ghostwhite',borderRadius:'1.2rem',padding:'3px',paddingRight:'0px'}}>
       <img src={img} height={"65px"} width={"65px"}></img>
  </button></Link>

  <h3 className="" style={{textAlign:'center' ,color:'white',marginTop:'-4.5rem',marginBottom:'.5rem'}}>Your Notes</h3>

  <div className="container"style={{display:"grid",gridTemplateColumns:"1fr 1fr"}}>


  {notes && notes.map((note)=>{
    
    
    return(




      


  
      <NoteItems key={note._id} note={note} handleButtonClick={handleButtonClick} />

    )
    
    
  })}


</div>





{/* <Route exact path="/createnote" element={<Addnote/>}/> */}




</>







);












}

export default Notes;
