import React ,{useContext} from "react";
import NoteContext from "../context/noteContext";
import NoteItems from "./NoteItems";


const Notes=()=>{

    const context= useContext(NoteContext);
 

    //destructing of context values
    const {notes,addnote}=context;

return(

<>


<button type="button" className="btn btn_primary">
       <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-file-earmark-plus" viewBox="0 0 16 16">
  <path d="M8 6.5a.5.5 0 0 1 .5.5v1.5H10a.5.5 0 0 1 0 1H8.5V11a.5.5 0 0 1-1 0V9.5H6a.5.5 0 0 1 0-1h1.5V7a.5.5 0 0 1 .5-.5"/>
  <path d="M14 4.5V14a2 2 0 0 1-2 2H4a2 2 0 0 1-2-2V2a2 2 0 0 1 2-2h5.5zm-3 0A1.5 1.5 0 0 1 9.5 3V1H4a1 1 0 0 0-1 1v12a1 1 0 0 0 1 1h8a1 1 0 0 0 1-1V4.5z"/>
</svg>
  </button>

  <h3 className="my-2 mt-4" style={{textAlign:'center' ,color:'white'}}>Your Notes</h3>

  <div className="container"style={{display:"grid",gridTemplateColumns:"1fr 1fr"}}>


  {notes.map((note)=>{
    
    
    return(

      <NoteItems key={note._id} note={note} />

    )
    
    
  })}


</div>


</>







);












}

export default Notes;
