import React, { useState } from 'react';
import '../Login-signup.css';
import './AuthForm.css';

const Login_Signup = () => {
  const [isLogin, setIsLogin] = useState(true);

  const switchToSignup = () => {
    setIsLogin(false);
  };

  const switchToLogin = () => {
    setIsLogin(true);
  };

  const handleSignupLink = (e) => {
    e.preventDefault();
    setIsLogin(false);
  };

  return (
    <div className="wrapper">
      <div className="title-text">
        <div className="title login">Login Form</div>
        <div className="title signup">Signup Form</div>
      </div>
      <div className="form-container">
        <div className="slide-controls">
          <label
            className={`slide login ${isLogin ? 'active' : ''}`}
            onClick={switchToLogin}
          >
            Login
          </label>
          <label
            className={`slide signup ${!isLogin ? 'active' : ''}`}
            onClick={switchToSignup}
          >
            Signup
          </label>
          <div
            className="slider-tab"
            style={{ left: isLogin ? '0%' : '50%' }}
          ></div>
        </div>
        <div className="form-inner" style={{ marginLeft: isLogin ? '0%' : '-50%' }}>
          <form className="login">
            <div className="field">
              <input type="text" placeholder="Email Address" required />
            </div>
            <div className="field">
              <input type="password" placeholder="Password" required />
            </div>
            <div className="pass-link"><a href="#">Forgot password?</a></div>
            <div className="field btn">
              <div className="btn-layer"></div>
              <input type="submit" value="Login" />
            </div>
            <div className="signup-link">
              Not a member? <a href="#" onClick={handleSignupLink}>Signup now</a>
            </div>
          </form>

          <form className="signup">
            <div className="field">
              <input type="text" placeholder="Email Address" required />
            </div>
            <div className="field">
              <input type="password" placeholder="Password" required />
            </div>
            <div className="field">
              <input type="password" placeholder="Confirm password" required />
            </div>
            <div className="field btn">
              <div className="btn-layer"></div>
              <input type="submit" value="Signup" />
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login_Signup;
