import React, { useState } from "react";
import NoteContext from "./noteContext";


//creating a notestate and sending to the NoteContext provider
const NoteState=(props)=>{

const initalNotes=[
    {
        "_id": "6807b17bf970cea87ab4a9dc",
        "user": "6807039a4c516ec1acd60895",
        "title": "Title is necessary",
        "description": "Description is also necessarty",
        "tag": "tag is not necessary",
        "date": "2025-04-22T15:10:51.750Z",
        "__v": 0
      },
      {
        "_id": "6809a7fc38d4fdc49c20f292",
        "user": "6807039a4c516ec1acd60895",
        "title": "Update title",
        "description": "Update Description",
        "tag": "Updated Tag",
        "date": "2025-04-24T02:54:52.223Z",
        "__v": 0
      },
      {
        "_id": "6809a82138d4fdc49c20f294",
        "user": "6807039a4c516ec1acd60895",
        "title": "new title",
        "description": "new Description",
        "tag": "Updated New Tag",
        "date": "2025-04-24T02:55:29.350Z",
        "__v": 0
      }


    ]

    
    const [notes,setnotes]=useState(initalNotes);

    //to update the UseContextApi after 1.5 second
    const update=()=>{
          setTimeout(() => {
              
              setState({
              
                  "name":"Samir",
                  "class":"BSC.CSIT "
              });

          }, 1500);
        


    }
    return(

<NoteContext.Provider value={{state,update}}>

{props.children}
    
</NoteContext.Provider>




    );
   




}

export default NoteState;
